mapper.select:
SELECT id, product_id, product_num, user_id
FROM CART
WHERE user_id = #{userId}

mapper.insert:
UPDATE CART
SET product_num = #{productNum}
WHERE product_id = #{productId}

mapper.select:
SELECT id, product_id, product_num, user_id
FROM CART
WHERE product_id = #{productId} AND user_id = #{userId}
